import React, { Component } from "react";

class App extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      totalReactPackages: [],
      cityinput: " ",
    };
  }

  componentDidMount() {
    // Simple GET request using fetch
    fetch(
      "https://api.openweathermap.org/data/2.5/weather?q=alameda&appid=5641904b9f9d3a2b1751db682fa354ff&units=imperial"
    )
      .then((response) => response.json())
      .then((data) => this.setState({ totalReactPackages: data }));
  }

  handleChange(event) {
    console.log("In the handle Change Function");
    this.setState({ cityinput: event.target.cityinput });
  }
  handleSubmit(event) {
    console.log("In the function");
    console.log(event.target.cityinput);
    //this.setState({ cityInput: event.target.value });
  }

  searchForTheWeather = () => {
    fetch(
      "https://api.openweathermap.org/data/2.5/weather?q=Oakland&appid=5641904b9f9d3a2b1751db682fa354ff&units=imperial"
    )
      .then((response) => response.json())
      .then((data) => this.setState({ totalReactPackages: data }));
  }; // End SEarch for the weather

  render() {
    const { totalReactPackages } = this.state;
    return (
      <div className="card text-center m-3">
        <label htmlFor="WeatherInput"> City </label>
        <input
          id="WeatherInput"
          type="text"
          placeholder="Alameda "
          cityinput={this.state.cityinput}
        />

        <button value onClick={this.handleSubmit} onChange={this.handleChange}>
          Search
        </button>

        <h5 className="card-header">Daily Weather</h5>
        <div className="card-body">
          <br />
          City: {totalReactPackages.name ? totalReactPackages.name : " "}
          <br />
          Temp: {totalReactPackages.main ? totalReactPackages.main.temp : " "}
          <br />
          Feels Like:{""}
          {totalReactPackages.main ? totalReactPackages.main.feels_like : " "}
          {console.log(totalReactPackages)}
          <br />
          Description:{""}
          {totalReactPackages.main ? totalReactPackages.weather[0].main : " "}
          {console.log(
            totalReactPackages.main ? totalReactPackages.weather[0].main : " "
          )}
        </div>
      </div>
    );
  }
}

export default App;
