import React, { Component } from "react";
import "./App.css";

class App extends Component {
  constructor() {
    super();
    this.state = {
      weather: [],
      error: null,
      isLoaded: false,
      items: [],
    }; // end constructor
  }

  componentDidMount() {
    // Fetch Function Goes here
    fetch(
      "https://api.openweathermap.org/data/2.5/weather?q=alameda&appid=5641904b9f9d3a2b1751db682fa354ff"
    )
      .then((res) => res.json())
      .then(
        (result) => {
          this.setState({
            isLoaded: true,
            items: result.items,
          });
        },
        (error) => {
          this.setState({
            isLoaded: true,
            error,
          });
        }
      );
  } // End Component Did Mount
  //https://api.openweathermap.org/data/2.5/weather?q=alameda&appid=5641904b9f9d3a2b1751db682fa354ff

  changeDescription = () => {
    const a = [12, 13, 15];
    this.setState({ number: this.state.number + 1 });
    console.log(this.state.weather.main.temp);
  };

  render() {
    const { error, isLoaded, items } = this.state;
    if (error) {
      return <div> ERror: {error.message}</div>;
    } else if (!isLoaded) {
      return <div> Loading....</div>;
    } else {
      return (
        <div>
          <h1> Hello World </h1>
        </div>
      );
    }
  }
} // End Class

export default App;
